package java100;
import java.util.*;
public class dick14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int w=sc.nextInt();
		int h=sc.nextInt();
		for (int i=1;i<=w;i++){
		for (int j=1;j<=h;j++){
			System.out.print(i*j+"\t");
		}
		System.out.println("");
		}
	}

}
